const modalTriggers=document.querySelectorAll(".popup-trigger"),modalCloseTrigger=document.querySelector(".modal-close"),bodyBlackout=document.querySelector(".body-blackout"),bodyFixed=document.querySelector("body");modalTriggers.forEach(e=>{e.addEventListener("click",()=>{const{popupTrigger:o}=e.dataset,d=document.querySelector(`[data-popup-modal="${o}"]`);d.classList.add("is-visible"),bodyBlackout.classList.add("is-blacked-out"),bodyFixed.classList.add("body-fixed"),d.querySelector(".modal-close").addEventListener("click",()=>{d.classList.remove("is-visible"),bodyBlackout.classList.remove("is-blacked-out"),bodyFixed.classList.remove("body-fixed")}),bodyBlackout.addEventListener("click",()=>{d.classList.remove("is-visible"),bodyBlackout.classList.remove("is-blacked-out"),bodyFixed.classList.remove("body-fixed")})})});