window.addEventListener("scroll",()=>{document.body.style.setProperty("--scroll",window.pageYOffset/(document.body.offsetHeight-window.innerHeight))},!1);const mediaquery=window.matchMedia("(min-width: 1024px)"),head=document.querySelector("#header");mediaquery.matches&&window.addEventListener("scroll",(function(){window.pageYOffset>100?head.classList.add("small"):head.classList.remove("small")}));